/** @format */
import "./Card.css";
export const Card = (props) => {
  const isLarge = props.body.length > 200 ? true : false;
  return (
    <div className='post-card-container'>
      <div className='post-card-title'>
        <h1>{props.title}</h1>
      </div>
      <hr />
      <div className='post-card-body'>{isLarge ? props.body.substring(0, 200)+"..." : props.body}</div>
      <h3>By: {props.user}</h3>
    </div>
  );
};
