/** @format */

import axios from "axios";
import { useState } from "react";

export const Create = () => {
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const userId = localStorage.userId;
  const titleChangeHandler = (e) => {
      setTitle(e.target.value);
  }
  const buttonClickHandler = (e) => {
    axios.post("http://localhost:5000/post/create", {
      title: title,
      body: body,
      user: userId,
    });
  };
  return (
    <>
      <input placeholder='Title' onClick={titleChangeHandler} value={title} />
      <br />
      <textarea placeholder='Body' onClick={bodyChangeHandler} value={body} />
      <br />
      <button onClick={buttonClickHandler}>Create</button>
    </>
  );
};
